window.t={};t.SELF_CLOSING_TAGS=["area","base","br","col","command","embed","hr","img","input","keygen","link","meta","param","source","track","wbr"];t.sc_template="<<%= tag %> <% _.each(atts, function(value, key) { %><%= key %>='<%= value %>' <% }); %> />";t.ct_template="<<%= tag %> <% _.each(atts, function(value, key) { %><%= key %>='<%= value %>' <% }); %> >"+"<%= content %>"+"</<%= tag %>>";t.tag=function(e){if(arguments[1]&&typeof arguments[1]==="string"){content=arguments[1];atts=arguments[2]||{}}else{content=arguments[2]||"";atts=arguments[1]||{}}if(e){if(_.indexOf(t.SELF_CLOSING_TAGS,e,true)==-1){return _.template(t.ct_template,{tag:e,atts:atts,content:content})}return _.template(t.sc_template,{tag:e,atts:atts})}else{return""}};t.button=function(e,n){return t.tag("button",n,e)};t.div=function(e,n){return t.tag("div",n,e)};t.link=function(e,n,r){r=r||{};r.href=n;t.tag("a",r,e)};t.label=function(e,n){return t.tag("label",e,{"for":n})};t.input=function(e){return t.tag("input",e)};t.span=function(e,n){return t.tag("span",e,n)};t.formInput=function(e,n,r){var i;r=r||{};r.name=r.name||n;r.type=e;switch(e){case"text":case"password":return t.label(n,r.name)+t.input(r);break;case"radio":case"checkbox":return t.input(r)+t.label(n,r.name);break;case"option":return t.tag("option",r,n);break;case"textarea":i=r.value;delete r.value;return t.label(n,r.name)+t.div(t.tag("textarea",r,i));break;default:return t.span("UNIMPLEMENTED FORM INPUT TYPE '"+e+"'",r)}}